const computerChoices = ['r', 'p', 's'];
let wins = 0;
let losses = 0;
let ties = 0;
const gameRef = document.getElementById('game');

document.onkeyup = function(event) {
  const computerChoice = computerChoices[Math.floor((Math.random() * 3))];
  const userChoice = event.key;
  let userPic = '';
  let computerPic = '';
  switch (userChoice) {
    case 'r':
      userPic = 'data:image/jpeg;base64,';
      break;
    case 'p':
      userPic = 'data:image/jpeg;base64,'
      break;
    case 's':
      userPic = 'data:image/jpeg;base64,'
      break;
    default:
      userPic = 'data:image/jpeg;base64,'
      break;
  }
  switch (computerChoice) {
    case 'r':
      computerPic = 'data:image/jpeg;base64,';
      break;
    case 'p':
      computerPic = 'data:image/jpeg;base64,'
      break;
    case 's':
      computerPic = 'data:image/jpeg;base64,'
      break;
  }

  let userMessage = '';
  if(userChoice === 'r' || userChoice === 'p' || userChoice === 's') {
    if ((userChoice === 'r' && computerChoice === 's') || (userChoice === 'p' && computerChoice === 'r') || (userChoice === 's' && computerChoice === 'p')) {
      wins++;
      userMessage = 'User wins';
    } else if (userChoice === computerChoice) {
      ties++;
      userMessage = 'It\s a tie';
    } else {
      losses++;
      userMessage = 'You lose loser!';
    }
  } else {
    userMessage = 'Press the right key jackass!';
  }
  
  gameRef.innerHTML = `
    <h1>${userMessage}</h1>
    <div class="choices">
      <section>
        <h2>User Choice</h2>
        <img src="${userPic}">
      </section>
      <section>
        <h2>Computer Choice</h2>
        <img src="${computerPic}">
      </section>
    </div>
    <p>Wins: ${wins}</p>
    <p>Ties: ${ties}</p>
    <p>Losses: ${losses}</p>
  `;
};
